mapper.insert:

insert into sys_dept (dept_id, parent_id, ancestors, 
  dept_name, del_flag, create_by, 
  create_time, update_by, update_time
  )
values (#{deptId,jdbcType=BIGINT}, #{parentId,jdbcType=BIGINT}, #{ancestors,jdbcType=VARCHAR}, 
  #{deptName,jdbcType=VARCHAR}, #{delFlag,jdbcType=VARCHAR}, #{createBy,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:

where d.del_flag = '0'

  AND dept_id = #{deptId}


  AND parent_id = #{parentId}


  AND dept_name like concat('%', #{deptName}, '%')


  AND status = #{status}

order by d.parent_id, d.order_num

mapper.update:
update sys_dept
parent_id = #{parentId}, dept_name = #{deptName}, ancestors = #{ancestors}, order_num = #{orderNum}, leader = #{leader}, leader_id = #{leaderId}, phone = #{phone}, email = #{email}, status = #{status}, type = #{type}, update_by = #{updateBy}, update_time = sysdate()
where dept_id = #{deptId}

update sys_dept set ancestors =

  when #{item.deptId} then #{item.ancestors}

where dept_id in

  #{item.deptId}

mapper.select:

where d.del_flag = '0'
AND status = 0

  AND dept_id = #{deptId}


  AND parent_id = #{parentId}


  AND dept_name like concat('%', #{deptName}, '%')

order by d.parent_id, d.order_num
