mapper.select:



    AND menu_name like concat('%', #{menuName}, '%')


    AND visible = #{visible}


    AND status = #{status}


order by parent_id, order_num

select distinct m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.chain, m.`query`, m.visible, m.status, m.catalog_status, ifnull(m.perms,'') as perms, m.is_frame, m.is_cache, m.menu_type, m.icon, m.order_num, m.create_time from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id left join sys_user_role ur on rm.role_id = ur.role_id left join sys_role ro on ur.role_id = ro.role_id where ur.user_id = #{params.userId} and rm.type = 1
AND m.menu_name like concat('%', #{menuName}, '%')
AND m.visible = #{visible}
AND m.status = #{status}
order by m.parent_id, m.order_num

select m.menu_id from sys_menu m left join sys_role_menu rm on m.menu_id = rm.menu_id where rm.role_id = #{roleId} and rm.type = #{type}
and m.menu_id not in (select m.parent_id from sys_menu m inner join sys_role_menu rm on m.menu_id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.order_num

mapper.update:
update sys_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, path = #{path}, component = #{component}, chain = #{chain}, `query` = #{query}, is_frame = #{isFrame}, is_cache = #{isCache}, menu_type = #{menuType}, visible = #{visible}, status = #{status}, catalog_status = #{catalogStatus}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where menu_id = #{menuId}
